    def to_txt(self):
        # File path for the new text file
        file_path = 'sumary of week.txt'

        # Open the file in write mode ('w')
        with open(file_path, 'w', encoding='utf-8') as file11:
            pass
            # Write content to the file
            file11.write("this is sumery of current week\n")
            file11.write("Your week plans:.\n")
            for i in range(len(activities)):
                file11.write('{}-{} {}\n'.format(activities[i][2], activities[i][3], activities[i][0]))
            file11.write("your goals were:\n")
            for i in range(10):
                file11.write(goals[i] + " " + check_boxes[i] + '\n')
            file11.write("your thoughts:\n")
            file11.write(feedback_text + '\n')



